sql.if:
,
if(count(distinct case when r.type = 0 and r.create_by = #{userId} then r.id end) > 0,
1, 0) as is_up,
if(count(distinct case when r.type = 1 and r.create_by = #{userId} then r.id end) > 0,
1, 0) as is_down,
if(count(distinct case when r.type = 2 and r.create_by = #{userId} then r.id end) > 0,
1, 0) as is_star

mapper.select:
select b.id, b.title, b.description, b.cover, b.is_enabled, b.is_deleted, count(distinct case when r.type = 0 then r.id end) as up, count(distinct case when r.type = 1 then r.id end) as down, count(distinct case when r.type = 2 then r.id end) as star, count(distinct case when r.type = 3 then r.id end) + b.view as view, b.status, b.create_time, b.create_by, u.nickname as author_nickname
from blog b left join user u on u.id = b.create_by left join record r on r.is_deleted = 0 and r.blog_id is not null
and b.create_by = r.create_by
and b.id = r.blog_id
b.is_deleted = 0
and (b.title like concat('%', #{keywords}, '%') or b.description like concat('%', #{keywords}, '%') or b.content like concat('%', #{keywords}, '%'))
and b.status = 1 and b.create_by = #{createBy}
and r.type = 0 and r.create_by = #{createBy}
and r.type = 1 and r.create_by = #{createBy}
and b.status = 0 and b.create_by = #{createBy}
and b.status = 2 and b.create_by = #{createBy}
and r.type = 2 and r.create_by = #{createBy}
and r.type = 3 and r.create_by = #{createBy}
and b.status = 1
group by b.id

select b.id, b.title, b.description, b.cover, b.is_enabled, b.is_deleted, count(distinct case when r.type = 0 then r.id end) as up, count(distinct case when r.type = 1 then r.id end) as down, count(distinct case when r.type = 2 then r.id end) as star, count(distinct case when r.type = 3 then r.id end) + b.view as view, b.status, b.create_time, b.create_by, u.nickname as author_nickname
from blog b left join user u on u.id = b.create_by left join record r on r.is_deleted = 0 and r.blog_id is not null and b.create_by = r.create_by
b.is_deleted = 0
and b.title like concat('%', #{title}, '%')
and b.description like concat('%', #{description}, '%')
and u.nickname like concat('%', #{authorNickname}, '%')
and b.status = #{status}
and b.create_time >= #{createTime[0],javaType=date}
and b.create_time <= #{createTime[1],javaType=date}
group by b.id

select c.id as id, c.name as name
from category c,
     blog_category bc
where c.id = bc.category_id
  and bc.blog_id = #{id}

select content
from blog
where id = #{id}
