sql.if:
,
if((select count(r.id) from record r where r.user_id = #{userId} and r.blog_id = b.id and r.type = 0) > 0,
1, 0) as is_up,
if((select count(r.id) from record r where r.user_id = #{userId} and r.blog_id = b.id and r.type = 1) > 0,
1, 0) as is_down,
if((select count(r.id) from record r where r.user_id = #{userId} and r.blog_id = b.id and r.type = 2) > 0,
1, 0) as is_star

mapper.select:
select c.id as id, c.name as name
from category c,
     blog_category bc
where c.id = bc.category_id
  and bc.blog_id = #{id}

select b.id, b.title, b.description, b.cover, b.content,
b.status, b.create_time, u.id as u_id, u.nickname as u_nickname, u.avatar as u_avatar, u.is_admin as u_is_admin, u.is_disable as u_is_disable, u.create_time as u_create_time, (select count(r.id) from record r where r.user_id = b.author_id and r.blog_id is not null and r.type = 0) as up_count, (select count(r.id) from record r where r.user_id = b.author_id and r.blog_id is not null and r.type = 1) as down_count, (select count(blog.id) from blog where blog.author_id = b.author_id and blog.status = 1 and blog.is_deleted = false) as pass_count
from blog b left join user u on b.author_id = u.id where b.id = #{id}
