mapper.select:
select count(*)
from blog
where logic_delete = 1;

mapper.sql:
from blog where 1 = 1
and post_time >= from_unixtime(#{blogView.startTime} / 1000) and post_time <= from_unixtime(#{blogView.endTime} / 1000)
and title like concat('%',#{blogView.title},'%')
and `description` like concat('%',#{blogView.description},'%')
and `status` = #{blogView.status}
and `logic_delete` = #{blogView.logic_delete}
and `author_username` in (select username from user where nickname like concat('%',#{blogView.authorNickname},'%'))
order by #{orderBy}
desc
