mapper.sql:
from userVo where 1 = 1


        and register_time  >=  from_unixtime(#{userVo.startTime} / 1000) and
        register_time  <=  from_unixtime(#{userVo.endTime} / 1000)


        and username like concat('%',#{userVo.username},'%')


        and `nickname` like concat('%',#{userVo.nickname},'%')


        and `identity` = #{userVo.identity}


        and `status` = #{userVo.status}


        and `logic_delete` = #{userVo.logic_delete}



    order by #{orderBy}

desc
